循环计算天数
	function getCurrentDay(theMonth,theDay) {
		var currentDay = 0;
		for(var i = 0;i < monthDay.length;i++) {
			if(i < theMonth) {
				if(isLeapYear && i == 1) {
					currentDay = currentDay + 29;
				}
				else {
					currentDay = currentDay + monthDay[i];
				}
			}
			if(i == theMonth) {
				currentDay = currentDay + theDay;
			}
		}
		return currentDay;
	}

